package looping;

public class Patte {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		int row = 1;
		int val = 1
				
				
				
				;
		while(row<=n){
			int col = 1;
			while(col<=row){
				System.out.print(val+" ");
				val++;
				col++;
			}
			System.out.println("");
			row++;
		}

	}

}
